import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [auth, setAuth] = useState({
    accessToken: localStorage.getItem('access_token') || null,
    user: null,
  });

  useEffect(() => {
    if (auth.accessToken) {
      // Optionally fetch user info here if backend supports it
      // For now, just set user as logged in
      setAuth((prev) => ({ ...prev, user: { correo: 'user@example.com' } }));
    }
  }, [auth.accessToken]);

  const login = async (correo, contraseña) => {
    const params = new URLSearchParams();
    params.append('username', correo);
    params.append('password', contraseña);

    const response = await axios.post('/token', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
    });

    const { access_token } = response.data;
    localStorage.setItem('access_token', access_token);
    setAuth({ accessToken: access_token, user: { correo } });
  };

  const logout = () => {
    localStorage.removeItem('access_token');
    setAuth({ accessToken: null, user: null });
  };

  const isAuthenticated = !!auth.accessToken;

  return (
    <AuthContext.Provider value={{ auth, login, logout, isAuthenticated }}>
      {children}
    </AuthContext.Provider>
  );
};
