import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [form, setForm] = useState({ correo: '', contraseña: '' });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (form.correo === '' || form.contraseña === '') {
      setError('Por favor, complete todos los campos.');
      return;
    }
    setError('');
    try {
      const params = new URLSearchParams();
      params.append('username', form.correo);
      params.append('password', form.contraseña);

      const response = await axios.post('/token', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
      });

      const { access_token } = response.data;
      localStorage.setItem('access_token', access_token);
      alert('Inicio de sesión exitoso');
      // TODO: Redirect to protected page or home
    } catch (err) {
      setError('Correo o contraseña incorrectos');
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: '400px' }}>
      <h2 className="mb-4 text-center">Iniciar Sesión</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="correo" className="form-label">Correo</label>
          <input
            type="email"
            id="correo"
            name="correo"
            className="form-control"
            value={form.correo}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="contraseña" className="form-label">Contraseña</label>
          <input
            type="password"
            id="contraseña"
            name="contraseña"
            className="form-control"
            value={form.contraseña}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary w-100">Ingresar</button>
      </form>
    </div>
  );
}

export default Login;
